import React, { Component } from 'react';

class AddKnifeForm extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            input: '',
            name: '',
            visibility: false,
            picture: '',
            pricePaid: 0,
            wouldSalePrice: 0,
            listForSale: false
        };
        this.toggleVisibility = this.toggleVisibility.bind(this);
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {
        this.setState({
          input: event.target.value
        });
      }

      handleSubmit(event) {
        event.preventDefault()
        this.setState({
          name: this.state.input,
          nameArray: this.state.input + this.state.nameArray,
          input: ''
        });
      }

    toggleVisibility() {
        this.setState(state => ({
            visibility: !state.visibility
        }));
    }

    render() {
        if (this.state.visibility) {
            return (
                <div>
                    <button className='dashboardLinkStyling' onClick={this.toggleVisibility}>Finished</button>
                    <form onSubmit={this.handleSubmit}>
                         <input value={this.state.input} onChange={this.handleChange} />
                        {/* <input>Picture</input> */}
                        {/* <input>Price Paid</input> */}
                        {/* <input>Would Sale Price</input> */}
                        {/* <checkbox>For Sale</checkbox> */}
                        <button type='submit'>Submit</button>
                    </form>
                    <h2>{this.state.nameArray.Join(', ')}</h2>
                </div>
            );
        } else {
            return (
                <div>
                    <button className='dashboardLinkStyling' onClick={this.toggleVisibility}>Add a Knife</button>
                </div>
            );
        }
    };
}

export default AddKnifeForm;
