import React from 'react'
import { render } from 'node-sass';




















// const saleTrue = ({ knives }) => {
    // const saleTrue = ({ this.knives });

    class ForSaleKnives extends React.Component {
        state = {
            knives: []
        }

        componentDidMount() {
            fetch('http://localhost:3000/api/knives?access_token=5e3600914e63efce06c8cee3')
              .then(res => res.json())
              .then((data) => {
                this.setState({ knives: data })
              })
              .catch(console.log)
            }

            render() {
                // const saleTrue = {knives};
            return (
                <div>
                    {/* {saleTrue.filter(saleTrue => saleTrue.includes(this.knives.forSale === true)).map(filteredName => (
                        <li>
                            {filteredName}
                        </li>
                    ))} */}
                    <center><h2 className='allKnivesStyling'>Knives for Sale:</h2></center>
                    <table className='container showStopper nice'>
                        <tbody>
                            <tr>
                                <td className=''>Brand</td>
                                <td className=''>Model</td>
                                <td className=''>Price</td>
                            </tr>
                        </tbody>
                    </table>
                    {this.knives.map((knife) => (
                        <div key={knife.id} align="start">
                            <table className='container tableBackground showStopper'>
                                <thead>
                                </thead>
                                <tbody>
                                    <tr>
                                        <td className='show1 shane'>{knife.brand}</td>
                                        <td className='show1 shane'>{knife.model}</td>
                                        <td className='show1 shane'>${knife.forSalePrice}</td>
                                    </tr>
                                </tbody>
                            </table>
                        </div>
                    ))}
                </div>
            )
        }
    };

    export default ForSaleKnives
