import React from 'react';
import './style.css';

class Brand extends React.Component {
    constructor() {
        super();
        this.state = {
            displayMenu: false,
            displayStyle: false,
            brand: "",
            titleDisplay: true,
            knifeData: [],
        };

        this.showDropdownMenu = this.showDropdownMenu.bind(this);
        this.hideDropdownMenu = this.hideDropdownMenu.bind(this);
        this.save = this.save.bind(this);
        // this.componentDidMount = this.componentDidMount.bind(this);
    };

    showDropdownMenu(event) {
        event.preventDefault();
        this.setState({ displayMenu: true }, () => {
            document.addEventListener('click', this.hideDropdownMenu);
        });
    }

    hideDropdownMenu(event) {
        this.setState({ displayMenu: false }, () => {
            document.removeEventListener('click', this.hideDropdownMenu);
        });
    }
    onHandleSave = (event) => {
        event.prevent.Default();

        let brand = event.target.brand.value;
        // let model = event.target.model.value;
        // let price = event.target.price.value;
        // let steel = event.target.steel.value;
        // let bladeShape = event.target.bladeShape.value;
        // let forSale = event.target.forSale.value;
        // let forSalePrice = event.target.forSalePrice.value;

        const knifeData = {
            brand, model, price, steel, bladeShape, forSale, forSalePrice
        };
    }

    // componentDidMount() {
    //     post('http://localhost:3000/api/blades?access_token=5e3600914e63efce06c8cee3')
    //       .then(res => res.json())
    //       .then((knifeData) => {
    //         this.setState({ blades: knifeData })
    //       })
    //       .catch(console.log)
    //   }

    save(event) {
        event.preventDefault();
        this.setState({ displayStyle: true, brand: this.knifeData.brand, titleDisplay: false, fixedBlade: true }, () => {
            document.addEventListener('click', this.hideDropdownMenu);
        });
    }

    render() {
            return (

                <div className="dropdown">
                {this.state.titleDisplay ? (
                    <div>
                        <input placeholder='Brand' onBlur={this.save} type='text' name='text'></input>
                    </div>
                ) :
                    (
                        null
                    )
                }

                {this.state.displayMenu ? (
                    <div className='dropStyling help1'>
                        {this.state.brand}
                    </div>
                ) :
                    (
                        null
                    )
                }
                {this.state.displayStyle ? (
                    <div>
                        <div className='styleStyling dropdownStylingTitle'>{this.state.brand}</div>
                    </div>
                ) :
                    (
                        null
                    )
                }
            </div>
        );
    }
}

export default Brand;
