import React from 'react';
import './style.css';
import ReactDOM from 'react-dom';

class Brand extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            brand: props.brand,
            model: props.model,

            displayMenu: false,
            displayStyle: false,

            titleDisplay: true,
            fixedBlade: false,
            slipJoint: false,
            flipper: false,
            automatic: false,
            assisted: false,
            thumbStud: false,
        };

        this.showDropdownMenu = this.showDropdownMenu.bind(this);
        this.onInputChange = this.onInputChange.bind(this);
        this.onSelectChange = this.onSelectChange.bind(this);
        this.save = this.save.bind(this);
        // this.hideDropdownMenu = this.hideDropdownMenu.bind(this);
    };

    showDropdownMenu(event) {
        event.preventDefault();
        this.setState({ displayMenu: true }, () => {
            document.addEventListener('click', this.hideDropdownMenu);
        });
    }


    hideDropdownMenu() {
        this.setState({ displayMenu: false }, () => {
            document.removeEventListener('click', this.hideDropdownMenu);
        });
    }



onInputChange(e) {
    this.setState({ brand: e.target.value });
}

onSelectChange(e) {
    this.setState({ model: e.target.value });
}

save(event) {

    event.preventDefault();
         this.setState({ displayStyle: true, brand: this.state.brand, titleDisplay: false }, () => {
             this.props.save(this.state.brand, this.state.model);
             document.addEventListener('click', this.hideDropdownMenu);
        });

}
    // onSelectChange(e) {
    //     this.setState({ brand: e.target.value });
    // }

    // save(event) {
    //     event.preventDefault();
    //     this.setState({ displayStyle: true, brand: this.state.brand, titleDisplay: false, fixedBlade: true }, () => {
    //         document.addEventListener('click', this.hideDropdownMenu);
    //     });
    // }
    render() {
        return (

            <div className="dropdown">
                {this.state.titleDisplay ? (
                    // <div className='dropdownStylingTitle' onClick={this.showDropdownMenu}>Select Knife Brand</div>
                    <div>
                    <input placeholder='Brand' id='brand' type='text' name='text'></input>
                    <button onClick={this.save}>Save</button>
                    </div>
                ) :
                    (
                        null
                    )
                }

                {this.state.displayMenu ? (
                    <div className='dropStyling help1'>
                       {this.state.brand}
                    </div>
                ) :
                    (
                        null
                    )
                }
                {this.state.displayStyle ? (
                    <div>
                        <div className='styleStyling dropdownStylingTitle'>{this.state.brand}</div>
                    </div>
                ) :
                    (
                        null
                    )
                }
            </div>
        );
    }
}

// ReactDOM.render(displayDropdown, document.getElementById('root'));
export default Brand;
