import React from 'react';
import './style.css';
import ReactDOM from 'react-dom';
import { Link } from 'react-router-dom';
import Dropdown from '../components/dropdown';

class KnifeStyleDropDown extends React.Component {
  constructor() {
    super();

    this.state = {
      displayMenu: false,
    };

    this.showDropdownMenu = this.showDropdownMenu.bind(this);
    this.hideDropdownMenu = this.hideDropdownMenu.bind(this);
  };

  showDropdownMenu(event) {
    event.preventDefault();
    this.setState({ displayMenu: true }, () => {
      document.addEventListener('click', this.hideDropdownMenu);
    });
  }

  hideDropdownMenu() {
    this.setState({ displayMenu: false }, () => {
      document.removeEventListener('click', this.hideDropdownMenu);
    });
  }

  render() {
    return (
      <div className="dropdown">
        <div className='dropDownStyling' onClick={this.showDropdownMenu}>Select Knife Style</div>

        {this.state.displayMenu ? (
          <div className='dropStyling help1'>
          <ul>
            <li className='knifeStyleDropdown'>Fixed Blade</li>
            <li className='knifeStyleDropdown'>Slip Joint</li>
            <li className='knifeStyleDropdown'>Flipper</li>
            <li className='knifeStyleDropdown'>Automatic</li>
            <li className='knifeStyleDropdown'>Assisted</li>
          </ul>
          </div>
        ) :
          (
            null
          )
        }
      </div>
    );
  }
}

var displayDropdown = (
  <div>
    <Dropdown />
  </div>
);

ReactDOM.render(displayDropdown, document.getElementById('root'));
export default KnifeStyleDropDown;
