import React, { Component } from 'react';

class AddKnifeForm extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            input: '',
            name: '',
            visibility: false,
            picture: '',
            pricePaid: 0,
            wouldSalePrice: 0,
            listForSale: false
        };
        // this.handleClick = this.handleClick.bind(this);
        this.toggleVisibility = this.toggleVisibility.bind(this);
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }
    // handleClick() {
    //     this.setState({
    //         name: ''
    //     });
    // }
    handleChange(event) {
        this.setState({
          name: event.target.value
        });
      }

      handleSubmit(event) {
        event.preventDefault()
        this.setState({
          submit: this.state.input,
          name: ''
        });
      }

    toggleVisibility() {
        this.setState(state => ({
            visibility: !state.visibility
        }));
    }

    render() {
        if (this.state.visibility) {
            return (
                <div>
                    <button className='dashboardLinkStyling' onClick={this.toggleVisibility}>Save a Knife</button>
                    <form onSubmit={this.handleSubmit}>
                         <input value={this.state.name} onChange={this.handleChange} />
                        {/* <input>Picture</input> */}
                        {/* <input>Price Paid</input> */}
                        {/* <input>Would Sale Price</input> */}
                        <checkbox>For Sale</checkbox>
                        <button type='submit'>Submit</button>
                    </form>
                </div>
            );
        } else {
            return (
                <div>
                    <button className='dashboardLinkStyling' onClick={this.toggleVisibility}>Add a Knife</button>
                </div>
            );
        }
    };
}

export default AddKnifeForm;
