import React, { Component } from "react";
import axios from "axios";

class Post extends Component {
  state = knives [
      {
    brand: "",
    model: "",
    steel: "",
    bladeShape: "",
    forSale: false,
    price: 0,
    forSaleprice: 0
  }
]

  onTitleChange = e => {
    this.setState({
      brand: e.target.value,
      model: e.target.value,
      steel: e.target.value,
      bladeShape: e.target.value,
      forSale: e.target.value,
      price: e.target.value,
      forSalePrice: e.target.value
    });
  };

  onBodyChange = e => {
    this.setState({
      brand: e.target.value,
      model: e.target.value,
      steel: e.target.value,
      bladeShape: e.target.value,
      forSale: e.target.value,
      price: e.target.value,
      forSalePrice: e.target.value,
    });
  };

  handleSubmit = e => {
    e.preventDefault();
    const data = {
      brand: this.state.brand,
      model: this.state.model,
      steel: this.state.steel,
      bladeShape: this.state.bladeShape,
      forSale: this.state.forSale,
      price: this.state.price,
      forSalePrice: this.state.forSalePrice,
    };
    axios
      .post("http://localhost:3000/api/knives?access_token=5e3600914e63efce06c8cee3", data)
      .then(res => console.log(res))
      .catch(err => console.log(err));
  };

  render() {
    return (
      <div className="post">
        <form className="post" onSubmit={this.handleSubmit}>
          <input
            placeholder="Brand" value={this.state.brand}
            onChange={this.onTitleChange} required
          />
          <input
            placeholder="Model" value={this.state.model}
            onChange={this.onTitleChange} required
          />
          <input
            placeholder="Steel" value={this.state.steel}
            onChange={this.onTitleChange} required
          />
          <input
            placeholder="Blade Shape" value={this.state.bladeShape}
            onChange={this.onTitleChange} required
          />
          <input
            placeholder="For Sale" value={this.state.forSale}
            onChange={this.onTitleChange} required
          />
          <input
            placeholder="Price" value={this.state.price}
            onChange={this.onTitleChange} required
          />
          <input
            placeholder="For Sale Price" value={this.state.forSaleprice}
            onChange={this.onTitleChange} required
          />
          <button type="submit">Create Post</button>
        </form>
      </div>
    );
  }
}

export default Post;

